    pub const Effect = enum(u8) {
        None,
        // onBeginMove
        Confusion,
        Conversion,
        FocusEnergy,
        Haze,
        Heal,
        LeechSeed,
        LightScreen,
        Mimic,
        Mist,
        Paralyze,
        Poison,
        Reflect,
        Splash,
        Substitute,
        SwitchAndTeleport,
        Transform,
        // onEnd
        AccuracyDown1,
        AttackDown1,
        DefenseDown1,
        DefenseDown2,
        SpeedDown1,
        AttackUp1,
        AttackUp2,
        Bide,
        DefenseUp1,
        DefenseUp2,
        EvasionUp1,
        Sleep,
        SpecialUp1,
        SpecialUp2,
        SpeedUp2,
        // isSpecial
        DrainHP,
        DreamEater,
        Explode,
        JumpKick,
        PayDay,
        Rage,
        Recoil,
        Charge,
        SpecialDamage,
        SuperFang,
        Swift,
        Thrashing,
        Trapping,
        // isMulti
        DoubleHit,
        MultiHit,
        Twineedle,
        // other
        AttackDownChance,
        DefenseDownChance,
        SpeedDownChance,
        SpecialDownChance,
        BurnChance1,
        BurnChance2,
        ConfusionChance,
        FlinchChance1,
        FlinchChance2,
        FreezeChance,
        ParalyzeChance1,
        ParalyzeChance2,
        PoisonChance1,
        PoisonChance2,
        Disable,
        HighCritical,
        HyperBeam,
        Metronome,
        MirrorMove,
        OHKO,

        comptime {
            assert(@sizeOf(Effect) == 1);
        }

        pub inline fn onBegin(effect: Effect) bool {
            return @enumToInt(effect) > 0 and @enumToInt(effect) <= 16;
        }

        pub inline fn isStatDown(effect: Effect) bool {
            return @enumToInt(effect) > 16 and @enumToInt(effect) <= 21;
        }

        pub inline fn onEnd(effect: Effect) bool {
            return @enumToInt(effect) > 16 and @enumToInt(effect) <= 31;
        }

        pub inline fn alwaysHappens(effect: Effect) bool {
            return @enumToInt(effect) > 31 and @enumToInt(effect) <= 38;
        }

        pub inline fn isSpecial(effect: Effect) bool {
            // NB: isSpecial includes isMulti up to Twineedle
            return @enumToInt(effect) > 31 and @enumToInt(effect) <= 46;
        }

        pub inline fn isMulti(effect: Effect) bool {
            return @enumToInt(effect) > 44 and @enumToInt(effect) <= 47;
        }

        pub inline fn isStatDownChance(effect: Effect) bool {
            return @enumToInt(effect) > 47 and @enumToInt(effect) <= 51;
        }

        pub inline fn isSecondaryChance(effect: Effect) bool {
            // NB: isSecondaryChance includes isStatDownChance as well as Twineedle
            return (@enumToInt(effect) > 46 and @enumToInt(effect) <= 61);
        }
    };

    const Target = enum(u4) {
        // none
        All,
        AllySide,
        Field,
        Self,
        // resolve
        AllOthers,
        Depends,
        Other,
        // TODO: resolve or resolve + run?
        Any,
        Allies,
        Ally,
        AllyOrSelf,
        Foe,
        // resolve + run
        Foes,
        FoeSide,
        RandomFoe,

        pub inline fn resolves(target: Target) bool {
            return @enumToInt(target) >= @enumToInt(Target.AllOthers);
        }

        pub inline fn runs(target: Target) bool {
            return @enumToInt(target) >= @enumToInt(Target.Foes);
        }
    };

